select
,
, tem.market_id , (SELECT coalesce(smt.name, sm.name) FROM spm_market_b sm LEFT OUTER JOIN spm_market_tl smt ON (sm.market_id = smt.market_id AND smt.lang = #{request.locale,jdbcType=VARCHAR,javaType=java.lang.String}) WHERE tem.market_id = sm.market_id) MARKET_NAME, (select coalesce(scvt.meaning, scvb.meaning) from sys_code_b scb, sys_code_value_b scvb left outer join sys_code_value_tl scvt ON (scvb.code_value_id = scvt.code_value_id AND scvt.lang = #{request.locale,jdbcType=VARCHAR,javaType=java.lang.String}) where scb.code = 'SYS.MES_TEM_TYPE' and scb.code_id = scvb.code_id and scvb.value = tem.TEMPLATE_CODE ) MEANING from SYS_MESSAGE_TEMPLATE tem
and tem.TEMPLATE_ID = #{templateId,jdbcType=DECIMAL}
and tem.TEMPLATE_CODE like concat('%',concat(#{templateCode,jdbcType=VARCHAR},'%'))
and tem.TEMPLATE_TYPE = #{templateType,jdbcType=VARCHAR}
and tem.DESCRIPTION like concat('%',concat(#{description,jdbcType=VARCHAR},'%'))
and tem.market_id = #{marketId, jdbcType=DECIMAL}

select
,
, tem.market_id , (SELECT coalesce(smt.name, sm.name) FROM spm_market_b sm LEFT OUTER JOIN spm_market_tl smt ON (sm.market_id = smt.market_id AND smt.lang = #{request.locale,jdbcType=VARCHAR,javaType=java.lang.String}) WHERE tem.market_id = sm.market_id) MARKET_NAME from SYS_MESSAGE_TEMPLATE tem where tem.TEMPLATE_ID = #{templateId,jdbcType=DECIMAL}

select 

FROM SYS_MESSAGE_TEMPLATE tem


		tem.TEMPLATE_CODE = #{templateCode,jdbcType=VARCHAR}


		AND tem.MARKET_ID is null


		AND tem.MARKET_ID = #{marketId,jdbcType=DECIMAL}


		AND tem.TEMPLATE_ID != #{templateId, jdbcType=DECIMAL}
