mapper.select:
select ACCOUNT_ID, ACCOUNT_TYPE, ACCOUNT_CODE, USER_NAME, OBJECT_VERSION_NUMBER, REQUEST_ID, PROGRAM_ID, CREATION_DATE, CREATED_BY, LAST_UPDATED_BY, LAST_UPDATE_DATE, LAST_UPDATE_LOGIN, ATTRIBUTE_CATEGORY, ATTRIBUTE1, ATTRIBUTE2, ATTRIBUTE3, ATTRIBUTE4, ATTRIBUTE5, ATTRIBUTE6, ATTRIBUTE7, ATTRIBUTE8, ATTRIBUTE9, ATTRIBUTE10, ATTRIBUTE11, ATTRIBUTE12, ATTRIBUTE13, ATTRIBUTE14, ATTRIBUTE15, DESCRIPTION, ORGANIZATION from SYS_MESSAGE_ACCOUNT
and ACCOUNT_ID = #{accountId,jdbcType=DECIMAL}
and ACCOUNT_CODE like concat('%',concat(#{accountCode,jdbcType=VARCHAR},'%'))
and ACCOUNT_TYPE = #{accountType,jdbcType=VARCHAR}
and DESCRIPTION like concat('%',concat(#{description,jdbcType=VARCHAR},'%'))

select 

FROM SYS_MESSAGE_ACCOUNT acc


		acc.ACCOUNT_CODE = #{accountCode,jdbcType=VARCHAR}


		AND acc.MARKET_ID is null


		AND acc.MARKET_ID = #{marketId,jdbcType=DECIMAL}


		AND acc.ACCOUNT_ID != #{accountId, jdbcType=DECIMAL}
